import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import styles from "./AddUser.module.css";

const AddUser = (props) => {
  //---- useStates to collect user inputs
  const [enteredUserName, setEnteredUserName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");

  //----useState for input validation
  const [isValid, setIsValid] = useState(true);

  //----username handler
  const userNameChangedHandler = (event) => {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setEnteredUserName(event.target.value);
  };

  // age change handler
  const ageChangedHandler = (event) => {
    if (event.target.value.trim.length > 0 && event.target.value > 0) {
      setIsValid(true);
    }
    setEnteredAge(event.target.value);
  };

  //Add user handler
  const addUserHandler = (event) => {
    event.preventDefault();
    //----if state isValid is false then we should not proceed with handler

    if (enteredUserName.trim.length === 0 || enteredAge.trim().length === 0) {
      return;
    }
    if (enteredAge < 1) {
      return;
    }
    console.log("This part executes if inputs are valid");
    const userData = {
      userName: enteredUserName,
      age: enteredAge,
    };

    console.log(userData);

    //----Reseting the values
    setEnteredUserName("");
    setEnteredAge("");
  };
  return (
    <Card className={styles.input}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input
          id="username"
          value={enteredUserName}
          type="text"
          onChange={userNameChangedHandler}
        />

        <label htmlFor="age">Age (Years)</label>
        <input
          id="age"
          value={enteredAge}
          type="number"
          onChange={ageChangedHandler}
        />

        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
